import React, { Component } from 'react'
import { Line } from "react-chartjs-2";
import {Chart, CategoryScale, Filler, LinearScale, PointElement, LineElement, BarElement, Title, Tooltip, Legend} from "chart.js";

Chart.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  Filler,
  Title,
  Tooltip,
  Legend
);

export default function SparklineChart(props) {
    const lineChartData = {
        labels: props.labels.slice(0, 14),
        datasets: [
            {
                data: props.data.slice(0, 14),
                fill: true,
                borderColor: "rgba(0, 255, 95, 0.56)",
                backgroundColor: "rgba(0, 255, 95, 0.3)",
                tension: 0.1
            }
        ]
    };

    return(
        <Line data={lineChartData} options={lineChartOptions}/>
    );
};

export const lineChartOptions = {
    response: true,
    layout: {
      padding: {
        left: 10,
        right: 20,
        top: 20,
        bottom: 20,
      }
    },
    elements: {
      point: {
        radius: 4
      }
    },
    plugins: {
      legend: {
        display: false
      },
      title: {
        display: false
      },
      tooltip: true,
      decimation: true
    },
    scales: {
      y: {
        display: false
      },
      x: {
        title: {
          display: false
        },
        grid: {
          display: false
        }
      }
    }
};
