import React, { Component } from 'react'
import axios from 'axios'
import { MarketDisplay, MarketDiv } from './styles'

export default class MarketBar extends Component {
    state = {
        isLoading: false,
        hasError: false,
        hasData: false,
        marketDetails: ""
    }

    getCrypto = async () => {
        this.setState({isLoading: true, hasError: false, hasData: false})
        try {
            const { data } = await axios(`https://api.coingecko.com/api/v3/global`);
            this.setState({isLoading: false, marketDetails: data, hasData: true})
        } catch (error) {
            console.log(error);
            this.setState({isLoading: false, hasError: true})
        }
    }

    componentDidMount() {
        this.getCrypto();
        console.log(this.state)
    }

    componentDidUpdate() {
        console.log(this.state)
    }

    render() {
        const { isLoading, hasError, hasData, marketDetails } = this.state;
        const { data } = marketDetails;
        console.log(data);
        console.log(marketDetails);
        return (
            <>

            <MarketDiv>
                {!hasData &&
                <MarketDisplay>
                    {/* <span>{data.active_cryptocurrencies}</span> */}
                </MarketDisplay>}
            </MarketDiv>
            </>
        )
    }
}
