import React, { Component } from 'react'
import axios from 'axios'
import { MarketDisplay, MarketDiv } from './styles'

export default class MarketBar extends Component {
    state = {
        isLoading: false,
        hasError: false,
        marketDetails: ""
    }

    getCrypto = async () => {
        this.setState({isLoading: true, hasError: false})
        try {
            const { data } = await axios(`https://api.coingecko.com/api/v3/global`);
            this.setState({isLoading: false, marketDetails: data})
        } catch (error) {
            console.log(error);
            this.setState({isLoading: false, hasError: true})
        }
    }

    componentDidMount() {
        this.getCrypto();
    }

    componentDidUpdate() {

    }

    render() {
        const { active_cryptocurrencies, markets, total_market_cap, market_cap_percentage } = this.state.marketDetails;
        return (
            <MarketDiv>
                <MarketDisplay>
                    <span>{active_cryptocurrencies}</span>
                </MarketDisplay>
            </MarketDiv>
        )
    }
}
