import React, { Component } from 'react';
import axios from "axios";
import { TableDiv } from './styles';

export default class CoinTable extends Component {
    state = {
        isLoading: false,
        hasError: false,
        hasData: false,
        coinData: [],
    }

    getData = async () => {
        this.setState({isLoading: true, hasError: false, hasData: false});
        try {
            const { data } = await axios(`https://api.coingecko.com/api/v3/coins/markets?vs_currency=usd&order=market_cap_desc&per_page=250&page=1&sparkline=false&price_change_percentage=1h%2C24h%2C7d`);
            this.setState({isLoading: false, hasData: true, coinData: data});
        }
        catch (error) {
            console.log(error);
            this.setState({isLoading: false, hasError: true, hasData: false});
        }
    }

    componentDidMount() {
        this.getData();
    }

    componentDidUpdate() {
        console.log(this.state);
    }

    render() {
        return (
            <TableDiv>
            </TableDiv>
        )
    }
}
