import React, { Component } from 'react';
import axios from "axios";
import { TableDiv, CoinDisplayDiv, CoinImage, CoinRank } from './styles';

function CoinDisplay(props) {
    return(
        <CoinDisplayDiv>
            <CoinRank>{props.market_cap_rank}</CoinRank>
            <CoinImage src={props.image}></CoinImage>
            <span>{props.price}</span>
            <span>{props.hourchange.toFixed(2)}</span>
            <span>{props.daychange.toFixed(2)}</span>
            <span>{props.sevenchange.toFixed(2)}</span>
        </CoinDisplayDiv>
    )
}

export default class CoinTable extends Component {
    state = {
        isLoading: false,
        hasError: false,
        hasData: false,
        coinData: [],
    }

    getData = async () => {
        this.setState({isLoading: true, hasError: false, hasData: false});
        try {
            const { data } = await axios(`https://api.coingecko.com/api/v3/coins/markets?vs_currency=usd&order=market_cap_desc&per_page=5&page=1&sparkline=false&price_change_percentage=1h%2C24h%2C7d`);
            this.setState({isLoading: false, hasData: true, coinData: data});
        }
        catch (error) {
            console.log(error);
            this.setState({isLoading: false, hasError: true, hasData: false});
        }
    }

    componentDidMount() {
        this.getData();
    }

    componentDidUpdate() {
        console.log(this.state);
    }

    render() {
        const {coinData, hasData} = this.state;
        return (
            <>
            {hasData && (
                <TableDiv>
                    {coinData.map((element) => 
                        <CoinDisplay 
                        key={element.market_cap_rank}
                        market_cap_rank={element.market_cap_rank}
                        price={element.current_price}
                        image={element.image}
                        hourchange={element.price_change_percentage_1h_in_currency}
                        daychange={element.price_change_percentage_24h_in_currency}
                        sevenchange={element.price_change_percentage_7d_in_currency}
                        />
                    )}
                </TableDiv>
            )}
            </>
        )
    }
}
