import styled, { createGlobalStyle } from "styled-components";

export const GlobalStyle = createGlobalStyle`
    body {
        background: #1F2128;
        color: #FAFBFB;
        margin: 0;
    }
`;
export const Wrapper = styled.div`
    display: flex;
    flex-flow: column;
    align-content: flex-start;
    width: 100%;
`;
