export const lineChartOptions = {
    response: true,
    elements: {
      point: {
        radius: 4
      }
    },
    plugins: {
      legend: {
        display: false
      },
      title: {
        display: false
      },
      tooltip: true,
      decimation: true
    },
    scales: {
      y: {
        display: false
      },
      x: {
        title: {
          display: false
        },
        offset: true,
        grid: {
          display: false
        }
      }
    }
};

export const barChartOptions = {
    elements: {
      point: {
        radius: 4
      }
    },
    plugins: {
      legend: {
        display: false
      },
      title: {
        display: false
      },
      tooltip: true,
      decimation: true
    },
    scales: {
      y: {
        display: false
      },
      x: {
        title: {
          display: false
        },
        // offset: true,
        grid: {
          display: false
        }
      }
    }
};

export const convertToMoney = new Intl.NumberFormat('us-EN', {minimumFractionDigits: 0});
