import React, { Component } from 'react';
import axios from "axios";
import { Line } from "react-chartjs-2";
import { Chart, Filler } from "chart.js";
import styled from "styled-components";
import {Chart as ChartJS, CategoryScale, LinearScale, PointElement, LineElement, BarElement, Title, Tooltip, Legend} from "chart.js";

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  Title,
  Tooltip,
  Legend
);

Chart.register(Filler);

export const chartOptions = {
    elements: {
      point: {
        radius: 1
      }
    },
    plugins: {
      legend: {
        display: false
      },
      title: {
        display: false
      },
      tooltip: true
    },
    scales: {
      y: {
        display: false
      },
      x: {
        title: {
          display: false
        },
        offset: true,
        grid: {
          display: false
        }
      }
    }
};

export const Div = styled.div`
  height: 500px;
  width: 100%;
`;

export default class CryptoChart extends Component {
    state = {
        isLoading: false,
        hasError: false,
        hasData: false,
        dateLabels: [],
        dailyPrice: [],
    }

    getData = async () => {
        this.setState({isLoading: true, hasError: false, hasData: false});
        try {
            const { data } = await axios(`https://api.coingecko.com/api/v3/coins/bitcoin/market_chart?vs_currency=usd&days=30&interval=daily`);
            let dateLabels = data.prices.map((element) => {
                const date = new Date(element[0]).toLocaleString("en-gb", {
                    day: "numeric",
                    month: "2-digit"
                });
                return date;
            });
            let dailyPrice = data.prices.map((element) => element[1].toFixed(2));
            dateLabels = dateLabels.slice(0, -1);
            dailyPrice = dailyPrice.slice(0, -1);
            this.setState({isLoading: false, dateLabels: dateLabels, dailyPrice: dailyPrice, hasData: true});
        }
        catch (error) {
            console.log(error);
            this.setState({isLoading: false, hasData: false, hasError: true});
        }
    }

    componentDidMount() {
        this.getData();
    }

    componentDidUpdate() {
        console.log(this.state);
    }


    render() {
        const { hasData, dailyPrice, dateLabels} = this.state;
        const chartData = {
            labels: dateLabels,
            datasets: [
                {
                    data: dailyPrice,
                    borderColor: "rgba(0, 255, 95, 0.56)",
                    backgroundColor: "rgba(0, 255, 95, 0.3)",
                    tension: 0.1
                }
            ]
        };
        console.log(chartData);

        return (
        <>
            {hasData && (
                    <Line data={chartData} options={chartOptions}/>
            )}
        </>
        );
    }
}
