import styled from "styled-components";

export const MarketDiv = styled.div`
    display: flex;
    flex-flow: row;
    align-items: center;
    justify-content: center;
    width: 100%;
    background: #1F2128;
`;

export const MarketDisplay = styled.div`
    display: flex;
    flex-flow: row;
    width: 50%;
    height: 50px;
    align-items: center;
    justify-content: space-around;
    background: #191B1F;
    border-radius: 0 0 12px 12px;
`;

export const CoinSlider = styled.input`
  height: 10px;
  width: 75px;
  border-radius: 5px;
  background: ##2172E5;
`;
