import styled from "styled-components";

export const MarketDiv = styled.div`
    display: flex;
    flex-flow: row;
    align-items: center;
    justify-content: center;
    width: 100%;
    background: #1F2128;
    `

export const MarketDisplay = styled.div`
    display: flex;
    flex-flow: row;
    width: 50%;
    height: 50px;
    align-items: center;
    justify-content: space-around;
    background: #191B1F;
`
export const CoinSlider = styled.input`
  -webkit-appearance: none;
  height: 10px;
  border-radius: 5px;
  background: #EEEEEE;
  opacity: 0.7;

  &::-webkit-slider-thumb {
  -webkit-appearance: none;
  appearance: none;
  width: 20px;
  height: 20px;
  border-radius: 50%;
  background: #00ADB5;
}

&::-moz-range-thumb {
  width: 20px;
  height: 20px;
  border-radius: 50%;
  background: #00ADB5;
`
